mapper.select:
SELECT A.id AS coopID,A.name AS coopName,A.introduce,A.total_area ,A.img_url,A.portrait, A.cultivar,A.age,A.telephone,A.address,A.official_user_id AS adminID, A.official_user_name AS adminName,A.establish_date,A.photo_url,A.is_audit, A.comment ,D.role FROM field_coop A , ( SELECT B.user_id,GROUP_CONCAT(C.role_name) AS role FROM common_user_role_rel B , common_role C WHERE B.role_id = C.id GROUP BY B.user_id ) D WHERE A.official_user_id = D.user_id AND A.state= '0'
AND A.is_audit = #{is_audit}
AND A.id = #{coopID}
AND A.name LIKE CONCAT('%',#{coopName}, '%')
AND A.telephone LIKE CONCAT('%',#{tel}, '%')
AND A.official_user_id = #{userID}
AND A.official_user_name LIKE CONCAT('%',#{userName}, '%')
AND ( A.name LIKE CONCAT('%',#{keyWord}, '%') OR A.official_user_name LIKE CONCAT('%',#{keyWord}, '%') )
AND A.establish_date >= #{start_time} AND #{end_time} >= A.establish_date

SELECT count(*) AS AssistantNum FROM field_coop_member
WHERE assistant
='0' AND state='0' AND coop_id = #{coop_id}

SELECT count(*) AS Co_opMemberNum FROM field_coop_member
WHERE
state='0' AND coop_id = #{coop_id}

SELECT count(*) AS Co_opNoticeNum FROM field_coop A , common_notice B
WHERE
A.state='0' AND B.status= '1' AND B.notice_type='2' AND A.id = B.object_id  AND A.id = #{coop_id}

SELECT id,name,img_url,assistant,user_id FROM field_coop_member
WHERE assistant ='0' AND
state='0' AND coop_id = #{coop_id}

mapper.update:
UPDATE field_coop SET state = #{state}

	,name = #{name}



	,address = #{address}



	,img_url = #{img_url}



	,is_audit = #{is_audit}



	,comment = #{comment}


WHERE id = #{id}

UPDATE field_coop_member SET state = #{state}

	,name = #{name}


	,img_url = #{img_url}


	,sex = #{sex}


	,age = #{age}


	,telephone = #{telephone}


	,address = #{address}



	,plant_age = #{plant_age}



	,plant_cat_id = #{plant_cat_id}


	,plant_area = #{plant_area}


	,assistant = #{assistant}

WHERE id = #{id}

mapper.select:
select * from common_notice where notice_type = '1' and status='1'

	AND notice_content LIKE
	CONCAT(CONCAT('%',#{msg}),'%')


	AND notice_role_type = #{role}


	AND publish_time  >= 
	#{start_time}


	AND publish_time  <= 
	#{end_time}

SELECT notice_content,picture_url,publish_time  FROM common_notice
WHERE notice_type='2' AND status='1' AND object_id = #{coopID}
ORDER BY publish_time DESC
