mapper.select:
SELECT kvs.variety_id,kve.chinese_name ,kvs.record_time,kvs.sop_type,kvs.record_status,cbu.name FROM km_variety_sop kvs LEFT JOIN km_variety_encyclopedia kve ON kvs.variety_id=kve.variety_id LEFT JOIN common_backend_user cbu ON kvs.record_user_id = cbu.id WHERE sop_status=1
AND kvs.sop_type = #{sop_type}
AND kvs.record_status = #{record_status}

mapper.update:
UPDATE km_variety_sop
sop_type=#{sop_type}, record_user_id=#{record_user_id}, update_time=#{update_time},
WHERE variety_id = #{variety_id}

update.foreach:
update km_sop_step
step_name=#{item.step_name}, short_desc=#{item.short_desc}, detail_desc=#{item.detail_desc}, next_suggestion=#{item.next_suggestion}, recommendation=#{item.recommendation}, audio_url=#{item.audio_url}, example_url=#{item.example_url}
where sop_id = #{item.sop_id}
