mapper.select:
SELECT kvs.id,kve.variety_name chinese_name ,kvs.record_time,kvs.sop_type,kvs.record_status,cbu.name,cbu.id FROM km_variety_sop kvs LEFT JOIN common_medicine_variety kve ON kvs.variety_id=kve.variety_code LEFT JOIN common_backend_user cbu ON kvs.record_user_id = cbu.id WHERE sop_status=1
AND kvs.sop_type = #{sop_type}
AND kvs.record_status = #{record_status}
order by kvs.record_time DESC

mapper.update:
UPDATE km_variety_sop
sop_type=#{sop_type}, record_user_id=#{record_user_id}, update_time=now(), record_status = '2'
WHERE id = #{id}

update.foreach:
update km_sop_step
step_name=#{item.step_name}, short_desc=#{item.short_desc}, detail_desc=#{item.detail_desc}, next_suggestion=#{item.next_suggestion}, recommendation=#{item.recommendation}, audio_url=#{item.audio_url}, example_url=#{item.example_url}, status=1
where sop_id = #{id}) AND step_order=#{item.step_order}

mapper.update:
UPDATE km_sop_step SET status = 0
WHERE
sop_id = #{id}
	AND
	step_order = #{step_order}

UPDATE km_sop_step SET status = 0
WHERE
sop_id = #{id}
