mapper.update:
UPDATE km_variety_encyclopedia
`synopsis`=#{synopsis}, chinese_name=#{chinese_name}, latin_name=#{latin_name}, alias=#{alias}, two_law=#{two_law}, `circles`=#{circles}, door=#{door}, outline=#{outline}, item=#{item}, suborder=#{suborder}, `section`=#{section}, family=#{family}, genus=#{genus}, speices=#{speices}, subspecies=#{subspecies}, `distribution_area`=#{distribution_area}, graph_url=#{graph_url}, feature=#{feature}, habit=#{habit}, distrubtion_range=#{distrubtion_range}, variety=#{variety}, cultivation_tech=#{cultivation_tech}, main_value=#{main_value}, composition=#{composition}, record_status = '2', update_time= now()
WHERE id = #{id}

mapper.select:
SELECT a.id, a.chinese_name, a.variety_id, a.codex_status, a.record_status, a.record_time, CASE WHEN a.codex_status = 1 THEN '药典' ELSE '百科' END AS type FROM km_variety_encyclopedia a LEFT JOIN km_pharmacopoeia_information b ON a.variety_id = b.cat_code WHERE 1 = 1
and a.chinese_name LIKE concat('%',#{search_name},'%')
and codex_status = #{type_code}
and record_status= #{record_status}
ORDER BY record_time DESC

SELECT id, variety_id, synopsis, chinese_name, latin_name, alias, two_law, circles, door, outline, item, suborder, section, family, genus, speices, subspecies, distribution_area, graph_url, feature, habit, distrubtion_range, variety, cultivation_tech, main_value, composition FROM km_variety_encyclopedia WHERE 1=1
and variety_id = #{variety_code}
and chinese_name = #{variety_name} OR alias LIKE concat('%',#{variety_name},'%')

mapper.update:
UPDATE km_audit
status=#{status}, fail_opinion=#{fail_opinion}, audit_time = now()
WHERE km_type = #{km_type} AND object_id = #{object_id}
