mapper.select:
SELECT mp.*,COUNT(mo.purchase_id) as offer ,mo.price,cu.name,cu.telephone FROM mall_purchase mp LEFT JOIN mall_offer mo ON mp.id = mo.purchase_id Left join common_user cu ON cu.id = mp.user_id WHERE mp.state = 1
and mp.is_aduit = #{is_aduit}
and mp.user_id = #{user_id}
and mp.purchase_cat_code = #{cat_code}
and mp.goods_cat_code = #{goods_cat_code}
and (mp.goods_name like CONCAT('%',#{goods_name},'%') or cu.name like CONCAT('%',#{goods_name},'%') )
GROUP BY mp.id,mp.goods_name,mp.specification,mp.purchase_quantity,mp.publish_time ORDER by mp.publish_time DESC

SELECT mp.*,COUNT(mo.purchase_id) as offer ,mo.price,cu.name,cu.telephone FROM mall_purchase mp LEFT JOIN mall_offer mo ON mp.id = mo.purchase_id Left join common_user cu ON cu.id = mp.user_id WHERE mp.state = '1' and mp.is_aduit = '1'
and mp.purchase_cat_code = #{cat_code}
and mp.goods_cat_code = #{goods_cat_code}
and (mp.goods_name like CONCAT('%',#{goods_name},'%'))
GROUP BY mp.id,mp.goods_name,mp.specification,mp.purchase_quantity,mp.publish_time ORDER by mp.publish_time DESC

mapper.update:
update mall_purchase SET is_aduit = '3' WHERE is_aduit='1'
and id IN 
#{id}
