mapper.update:
update mall_supply set
is_audit = #{result},carriage_status = '1',comment = #{comment} where
id = #{supplyId}

update mall_supply set state
= #{result} where id in
#{id}

mapper.select:
SELECT a.id, a.goods_name, b.NAME, a.supply_cat_code, b.telephone, a.specification, a.inventory, a.inventory_unit, a.price, a.price_unit, a.img_url, a.carriage_status, a.is_audit FROM mall_supply a LEFT JOIN common_user b ON a.seller_id = b.id where a.state = '0'
AND (a.goods_name like CONCAT('%',#{key},'%') OR b.telephone like CONCAT('%',#{key},'%') OR b.NAME like CONCAT('%',#{key},'%'))
AND a.is_audit = #{is_audit}
AND a.carriage_status = #{carriage_status}
AND a.supply_cat_code = #{goodType}

SELECT a.id, a.goods_name, b.NAME, a.purchase_cat_code, b.telephone, a.specification, a.purchase_quantity, a.unit, a.img_url, a.is_aduit FROM mall_purchase a LEFT JOIN common_user b ON a.user_id = b.id WHERE a.state = '1' AND a.is_aduit = '0'
AND (a.goods_name like CONCAT('%',#{key},'%') OR b.telephone like CONCAT('%',#{key},'%') OR b.NAME like CONCAT('%',#{key},'%'))
AND a.purchase_cat_code = #{goodType}
