SELECT a.* FROM ( SELECT id, notice_type, notice_content, publish_time FROM common_notice WHERE CONCAT(',',notice_receiver,',') LIKE CONCAT( CONCAT( '%', #{user_id} ), '%' ) AND notice_type = '2' AND STATUS = '1' AND state = '1' UNION SELECT id, notice_type, notice_content, publish_time FROM common_notice WHERE notice_role_type IN
#{notice_role_type_one}
AND STATUS = '1' AND state = '1' ) AS a ORDER BY publish_time DESC

select
GROUP_CONCAT(role_id order by role_id) as role_id from
common_user_role_rel where user_id = #{user_id}
