mapper.select:
select
distinct product_name from common_price where state = '1' and status =
'1' and cat_name = #{hotword}

select id,price_date,IFNULL(cat_name,'') AS cat_name,IFNULL(specifaction,'') AS specifaction,IFNULL(product_name,'') AS product_name, IFNULL(market_name,'') AS market_name,IFNULL(price,'') AS price,IFNULL(in_time,'') AS in_time,IFNULL(state,'') AS state,IFNULL(status,'') AS status from common_price where state = '1' and status = '1'
AND cat_name = #{hotword}
AND market_name = #{market}
ORDER BY price_date

SELECT
price_date,
( SUM( price ) / count( 1 ) ) AS price,
cat_name from
common_price where state = '1' and status = '1'

	AND cat_name = #{hotword}


	AND market_name = #{market}


	AND specifaction = #{specifaction}


	AND product_name = #{product}


	AND price_date  >= 
	#{start_time}


	AND price_date  <= 
	#{end_time}

GROUP BY
price_date

SELECT
price_date,
( SUM( price ) / count( 1 ) ) AS price,
cat_name from
common_price where state = '1' and status = '1'

	AND cat_name = #{hotword}


	AND market_name = #{market}


	AND specifaction = #{specifaction}


	AND product_name = #{product}


	AND price_date  >= 
	#{start_time}


	AND price_date  <= 
	#{end_time}

GROUP BY
price_date
ORDER BY
price_date
limit 0,9

SELECT
price_date,
( SUM( price ) / count( 1 ) ) AS price,
cat_name from
common_price where state = '1' and status = '1' and price_date =
#{time}

	AND cat_name = #{hotword}


	AND market_name = #{market}


	AND specifaction = #{specifaction}


	AND product_name = #{product}

GROUP BY
price_date
