mapper.select:
select
  id
from
  common_follow
where
  follow_flag = 1
 
     and object_type = #{type}
 
and object_id = #{attedUserId}
and follow_user_id = #{userId}

mapper.update:
update common_follow
  set follow_flag = 0
  where follow_user_id = #{userId}
  and object_id = #{attedUserId}
