mapper.select:
select log.id, log.user_id, log.op_type, log.op_resource, log.op_module, DATE_FORMAT( log.op_time, '%Y-%m-%d %H:%i:%S') as op_time,log.op_status, IFNULL(cu.name,"无名氏") as name from common_log log left join common_user cu on log.user_id = cu.id where 1 = 1
and cu.name like CONCAT('%',#{userName},'%')
and log.login_time >= #{starTime}
and log.login_time <= #{endTime}
and log.op_status = #{status}
order by op_time desc

select DATE_FORMAT(clo.login_time, '%Y-%m-%d') as login_time_f, count(1) as loginTimes from common_logon_log clo left join common_user cu on clo.user_id = cu.id where 1 = 1
and clo.login_time >= #{starTime}
and clo.login_time <= #{endTime}
and clo.login_time is not null group by login_time_f
