mapper.select:
SELECT ccu.id, ccu.loginid, ccu.name, DATE_FORMAT(ccu.update_time, '%Y-%m-%d') AS create_time, ccu.telephone, ccu.is_allowed, cro.role_name, cro.id as role_id, ccc.name as craete_name, ccu.`password`, ccu.email, ccu.sex
FROM common_backend_user ccu LEFT JOIN common_backend_user_role_r crr ON ccu.id = crr.backend_user_id LEFT JOIN common_backend_user_role cro ON crr.backend_role_id = cro.id left join common_backend_user ccc on cro.create_user_id = ccc.id where cro.create_user_id is not null
and cro.id = #{roleId}
and ccu.loginid like concat( '%', #{keyWord} , '%') or ccu.name like concat( '%', #{keyWord} , '%')
and ccu.id = #{opid}
order by create_time desc

mapper.update:
UPDATE common_backend_user
  set id = #{id}
      
        , loginid = #{loginId}
      
      
          , password = #{password}
      
      
          , name = #{name}
      
      
          , telephone = #{telephone}
      
      
          , is_allowed = #{isWork}
      
  where id = #{id}

        update common_backend_user_role_r
          set backend_role_id = #{backendRoleid}

        where backend_user_id = #{id}
